import React,{useState} from 'react';
import {Form, Button, Container} from 'react-bootstrap';
import { bookService } from '../services/BookService';
import { useHistory } from "react-router-dom";

const AddBookView = () => {
    let history = useHistory();
    const [book,setBook] = useState({});

    const AddBook = () => {
        bookService.persist(book)
          .then(respons => {
            history.push('/');
          })
    }
    return (
        <Container style= {{marginTop: '30px'}}>
            <Form onSubmit={AddBook}>
                <Form.Group >
                    <Form.Label>Book Title</Form.Label>
                    <Form.Control name="title" type="text" placeholder=" Title" value={book.title} />
                </Form.Group>
                <Form.Group >
                    <Form.Label>Book Author</Form.Label>
                    <Form.Control name="author" type="text" placeholder=" Author" value={book.author} />
                </Form.Group>
                <Form.Group >
                    <Form.Label>Book Publisher</Form.Label>
                    <Form.Control name="publisher" type="text" placeholder=" Publisher" value={book.publisher} />
                </Form.Group>
                <Form.Group >
                    <Form.Label>Number of Pages</Form.Label>
                    <Form.Control name="pages" type="text" placeholder=" Number of Pages" value={book.numberOfPages} />
                </Form.Group>
                <Form.Group >
                    <Form.Label>Book Language</Form.Label>
                    <Form.Control name="language" type="text" placeholder=" Language " value={book.language} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Book summary</Form.Label>
                    <Form.Control name="summary" as="textarea" rows="3" placeholder="Summary" value={book.summary} />
                </Form.Group>
                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>     
        </Container>
    );
}

export default AddBookView;
