import React,{useState,useEffect} from 'react';
import {Container,Form, Button} from 'react-bootstrap';
import { bookService } from '../services/BookService';
import { useParams } from "react-router-dom";


const EditBookView = () => {

    let {isbn} = useParams();
    const [books, setBooks] = useState([]);

    const changeValue = (event) => {
        let newBook = books ;
        newBook[event.target.name] = event.target.value;
        setBooks(newBook);
    }

    useEffect(() => {
      const findBook = () => {
        bookService.findBookByIsbn(isbn)
        .then(function(response){
          setBooks(response.data);
        });
      };
      findBook();
    },[isbn]);

    return (
        <Container style= {{marginTop: '30px'}}>
        <Form>
            <Form.Group >
                <Form.Label>Book Title</Form.Label>
                <Form.Control name="title" type="text" placeholder=" Title" value={books.title} onChange={changeValue}/>
            </Form.Group>
            <Form.Group >
                <Form.Label>Book Author</Form.Label>
                <Form.Control name="author" type="text" placeholder=" Author" value={books.author} onChange={changeValue}/>
            </Form.Group>
            <Form.Group >
                <Form.Label>Book Publisher</Form.Label>
                <Form.Control name="publisher" type="text" placeholder=" Publisher" value={books.publisher} onChange={changeValue}/>
            </Form.Group>
            <Form.Group >
                <Form.Label>Number of Pages</Form.Label>
                <Form.Control name="pages" type="text" placeholder=" Number of Pages" value={books.numberOfPages} onChange={changeValue}/>
            </Form.Group>
            <Form.Group >
                <Form.Label>Book Language</Form.Label>
                <Form.Control name="language" type="text" placeholder=" Language " value={books.language} onChange={changeValue}/>
            </Form.Group>
            <Form.Group>
                <Form.Label>Book summary</Form.Label>
                <Form.Control name="summary" as="textarea" rows="3" placeholder="Summary" value={books.summary} onChange={changeValue}/>
            </Form.Group>
            <Button variant="primary" type="submit">
                Submit
            </Button>
        </Form> 
    </Container>
    );
}

export default EditBookView;
